import React from 'react';
import Header from './Header';
import Footer from './Footer';
import "bootstrap/dist/css/bootstrap.min.css";
// import './PerEvent.css';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const loadScript = (src) => {
  return new Promise((resolve) => {
    const script = document.createElement("script");
    script.src = src;
    script.onload = () => {
      resolve(true);
    };
    script.onerror = () => {
      resolve(false);
    };
    document.body.appendChild(script);
  });
};

const displayRazorpay = async (amount, event_id, navigate) => {
  console.log(amount, event_id);
  const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js");

  if (!res) {
    alert("You are offline. Payment cannot proceed.");
    return;
  }

  const options = {
    key: "rzp_test_dEYhZg38SrkYMD", 
    currency: "INR",
    amount: amount * 100,
    name: "Event Booking",
    description: "Thanks for booking with us!",
    image: "https://res.cloudinary.com/dtdlad1ud/image/upload/v1707733051/uhwydfqry5wqwkaazbbk.jpg",
    handler: async function (response) {
      try {
        const result = await axios.post(
          "http://localhost:3046/api/v1/users/booking",
          { event_id },
          {
            headers: {
              Authorization: `Token ${sessionStorage.getItem("adminToken")}`,
            },
          }
        );
        toast.success(result.data.message);
        navigate("/mybooking");
      } catch (error) {
        console.error("Error in booking:", error);
        toast.error("Booking failed. Please try again.");
      }
    },
    prefill: {
      name: "name",
    },
  };

  const paymentObject = new window.Razorpay(options);
  paymentObject.open();
};

export const book = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { state } = location;
  const { event } = state || {};

  const handleBookTicket = () => {
    const token = sessionStorage.getItem('adminToken');
      if (!token) {
      toast.warn("You need to be logged in to proceed with the booking.");
      navigate('/signin');
    } else {
      displayRazorpay(event.price, event._id, navigate);
    }
  };

  return (
    <>
      <Header />
      <div className="backp">
        <img
          className="backp img-fluid d-flex"
          src={event?.image || 'https://res.cloudinary.com/dtdlad1ud/image/upload/v1707733051/uhwydfqry5wqwkaazbbk.jpg'}
          alt="Hero Background"
        />
        <section className="bg-light text-black">
          <h1>{event?.title}</h1>
          <p>â‚¹{event?.price}</p>
          <h3>Warm To Welcome You</h3>
          <hr />
          <h5 className="m-2"><strong>When and Where</strong></h5>
          <p>Date and Time</p>
          <h3>{event?.s_date} to {event?.e_date}</h3>
          <p>Location</p>
          <h3>{event?.location}</h3>
          <button className="justify-center btn btn-primary form-control mb-3" onClick={handleBookTicket}>
            Book Ticket
          </button>
        </section>
      </div>
      <Footer />
    </>
  );
};

export default PerEvent;
