import React, { useState } from 'react';
import axios from 'axios';

function ChangePassword() {
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  // const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false); // for button disable state
  const [errorMessage, setErrorMessage] = useState('');

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    // Validate if newPassword and confirmPassword match
    if (newPassword !== password) {
      setErrorMessage('New password and confirm password do not match.');
      return;
    }

    // Reset error message if passwords match
    setErrorMessage('');

    // Set loading to true to prevent multiple submissions
    setLoading(true);

    try {
      // Get user token (or any authentication token)
      

      // Make API call to change the password
      const response = await axios.post(
        'http://localhost:3046/api/v1/users/passwordChange', // Replace with your actual API endpoint
        {
          password,
          newPassword,
        },
        {
          headers: {
            Authorization:sessionStorage.getItem('userToken'), // Use token for authorization if needed
          },
        }
      );

      // Check if the password change was successful
      if (response.status === 200 && response.data.success) {
        alert('Password changed successfully');
        setCurrentPassword('');
        setNewPassword('');
        setConfirmPassword('');
    
      } else {
        setErrorMessage('Failed to change password');
        console.log(response.data);
      }
    } catch (error) {
      console.error('Error user password details:', error);
      const apiError = error.response?.data?.message || 'Failed to change password';
      setErrorMessage(apiError);
    } finally {
      setLoading(false); // Reset loading state
    }
  };

  return (
    <div className="container">
      <h2>Change Password</h2>
      <form onSubmit={handlePasswordChange}>
        <div className="form-group">
          <label>Current Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Enter current password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>New Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Confirm New Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Confirm new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>

        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Changing...' : 'Change Password'}
        </button>
      </form>
    </div>
  );
}

export default ChangePassword;
