import React from 'react';
import './Book.css';
import Header from './Header';
import Footer from './Footer';
import { useLocation } from 'react-router-dom';

function Book() {
  const location = useLocation();
  const { state } = location;

  const { event } = state || {};

  return (
    <>
      <Header />
      <div className='book'>
        <div className='col'>
          <div className="card">
            <img src={event.image || "/default-image.jpg"} className="card-img-top" alt={event.title || "Event Image"} />
            <div className="card-body">
              <h2 className="card-title">{event.title || "Event Title"}</h2>
              <strong>  ${event.price || 'N/A'}</strong>
              <p>{event?.description || 'No description available'}</p>
          
              {/* <div className="d-flex justify-content-end  mt-3">
                <button className="btn btn-primary">Book Ticket</button>
              
              </div> */}
              <hr />
              <h3><strong>When And Where</strong></h3>
              
                <strong>Date - Time</strong>
               <p> {event.s_date} to {event.e_date} | {event.s_time} - {event.e_time || 'N/A'}
                
                </p> 
              
              <strong>Location</strong>
              <p>{event.location || 'Location'}</p>
            
              <div className="d-flex justify-content-end  mt-3">
                <button className="btn btn-primary">Book Ticket</button>
              
              </div>
              
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Book;



