import React from 'react';
import './Book.css';
import Header from './Header';
import Footer from './Footer';
import axios from 'axios';
import Cookies from 'js-cookie';

import { useNavigate, useLocation } from 'react-router-dom';

function Book() {
  const location = useLocation();
  const { state } = location;
  const { event } = state || {};
  const navigate = useNavigate();

  const loadScript = (src) => {
    return new Promise((resolve) => {
      const script = document.createElement("script");
      script.src = src;

      script.onload = () => {
        resolve(true);
      };
      script.onerror = () => {
        resolve(false);
      };
      document.body.appendChild(script);
    });
  };

  const displayRazorpay = async (amount, event_id) => {
    // Check if the user is logged in by checking for the access token
    const accessToken = Cookies.get("accessToken");
  
    if (!accessToken) {
      // If no token is found, redirect the user to the sign-in page
      console.log("User is not logged in. Redirecting to sign-in.");
      navigate("/signin");
      return; // Stop further execution of this function
    }
  
    console.log(amount, event_id);
    const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js");
    if (!res) {
      alert("You are offline. Please check your internet connection.");
      return;
    }
  
    const options = {
      key: "rzp_test_dEYhZg38SrkYMD", // Razorpay API key
      currency: "INR",
      amount: amount * 100, // Razorpay requires the amount in paisa, so multiply by 100
      name: "Event Booking",
      description: "Thanks for booking your ticket.",
      image: "https://res.cloudinary.com/dtdlad1ud/image/upload/v1707733051/uhwydfqry5wqwkaazbbk.jpg",
      handler: async function (response) {
        // Handle successful payment here
        try {
          const bookingResponse = await axios.post(
            "http://localhost:3046/api/v1/users/booking",
            { event_id }, // Send the event_id to the backend
            {
              headers: {
                Authorization: accessToken, // Use the token from Cookies
              },
            }
          );
          alert(bookingResponse.data.message); // Show the success message
          navigate("/mybooking"); // Redirect to mybooking page after successful booking
        } catch (err) {
          console.error(err);
          alert("Something went wrong while booking.");
        }
      },
      prefill: {
        name: "User Name", // You can dynamically set the user name here
      },
    };
  
    const paymentObject = new window.Razorpay(options);
    paymentObject.open();
  };

  return (
    <>
      <Header />
      <div className='book'>
        <div className='col'>
          <div className="card">
            <img src={event.image || "/default-image.jpg"} className="card-img-top" alt={event.title || "Event Image"} />
            <div className="card-body">
              <h2 className="card-title">{event.title || "Event Title"}</h2>
              <strong>  â‚¹{event.price || 'N/A'}</strong>
              <p>{event?.description || 'No description available'}</p>
          
              <hr />
              <h3><strong>When And Where</strong></h3>
              
              <strong>Date - Time</strong>
              <p> {event.s_date} to {event.e_date} | {event.s_time} - {event.e_time || 'N/A'}</p>
              
              <strong>Location</strong>
              <p>{event.location || 'Location'}</p>
            
              <div className="d-flex justify-content-end mt-3">
                <button 
                  className="btn btn-primary" 
                  onClick={() => displayRazorpay(event.price, event._id)}>
                  Book Ticket
                </button>
              </div>
              
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Book;
