import React, { useState, useEffect } from 'react';
import './Account.css';
import Header2 from './Header2';
import 'font-awesome/css/font-awesome.min.css';

function Account() {
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const token = sessionStorage.getItem("userToken");
    console.log(token); // Check if the token is being retrieved correctly
    fetch('http://localhost:3046/api/v1/users/getcurrentUser', {
      headers: {
        Authorization: `${token}`
      }
    })
      .then(response => response.json())
      .then(data => {
        console.log("Current User Details:", data);
        if (data.success) {
          setUserDetails(data.data);
        }
      })
      .catch(error => console.error('Error fetching user details:', error));
  }, []);
  

  return (
    <>
      <Header2 />
      <div className='account row justify-content-center bg-dark'>
        <div className='acdetail col-md-6'>
          <h6 className='text-center'>Account Details</h6>
          <hr />
          <div className='d-flex justify-content-between text-primary'>
            <span><i className="fa fa-fw fa-home"></i> HOME</span>
            <span> CHANGE PASSWORD</span>
          </div>

          {u ? (
            <div className='acdetail2'>
              <h6 className='text-start'>Change Account Details</h6>
              <div className='he2icon2'>
                <a href='/admin/profile'>
                  <img className='adminicon' src={u.avatar} alt='Admin Icon' />
                </a>
              </div>
              <div className='text-white d-flex'>
                <i className="fa fa-fw fa-user"></i> {us.fullName}
                <div className='mt col text-end'>
                  <i className="fa fa-fw fa-book"></i>
                </div>
              </div>
              <div className='text-white d-flex'>
                <i className="fa fa-fw fa-envelope"></i> {userDetails.email}
                <div className='mt col text-end'>
                  <i className="fa fa-fw fa-book"></i>
                </div>
              </div>
              <div className='text-white d-flex'>
                <i className="fa fa-fw fa-venus-mars"></i> {userDetails.gender}
                <div className='mt col text-end'>
                  <i className="fa fa-fw fa-book"></i>
                </div>
              </div>
              <div className='text-white d-flex'>
                <i className="fa fa-fw fa-phone"></i> {userDetails.mobile_no}
                <div className='mt col text-end'>
                  <i className="fa fa-fw fa-book"></i>
                </div>
              </div>
            </div>
          ) : (
            <p className="text-white">Loading...</p>
          )}
        </div>
      </div>
    </>
  );
}

export default Account;
