import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ContactMessages.css';
import Cookies from 'js-cookie'; // If using cookies for storing token
import { useNavigate } from 'react-router-dom'; // To handle redirection

const ContactMessages = () => {
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Initialize navigate for redirection

  useEffect(() => {
    // Fetch data from the API with admin authorization
    const adminToken = sessionStorage.getItem("adminToken") || Cookies.get("adminToken");

    if (!adminToken) {
      setError('No admin token available. Please log in.');
      alert("You are not logged in as admin. Redirecting to login page.");
      navigate('/adminsignin'); // Redirect to admin login page if no token is found
      return;
    }

    axios.get('http://localhost:3046/api/v1/contact/allmessage', {
      headers: {
        Authorization: `Bearer ${adminToken}`, // Attach admin token with "Bearer"
      },
    })
    .then(response => {
      console.log('Full API Response:', response); // Log the entire response object
      if (response.data.success) {
        setMessages(response.data.data); // Assuming the data is in response.data.data
      } else {
        console.error('API Response Error:', response.data.message);
        setError(response.data.message || 'Unexpected error occurred.');
      }
    })
    .catch(error => {
      console.error('Error fetching messages:', error);
      setError('Error fetching messages.');
    });
  }, [navigate]);

  return (
    <div className="contact-messages-container">
      <h1>Contact Messages</h1>
      {error && <p className="error">{error}</p>}
      <div className="messages-list">
        {messages.length > 0 ? (
          messages.map((message, index) => (
            <div key={index} className="message-card">
              <h2>{message.fullName}</h2>
              <p><strong>Email:</strong> {message.email}</p>
              <p><strong>Mobile No:</strong> {message.mobile_no}</p>
              <p><strong>Message:</strong> {message.message}</p>
            </div>
          ))
        ) : (
          <p>No messages available.</p>
        )}
      </div>
    </div>
  );
};

export default ContactMessages;
