import React, { useState } from 'react';
import axios from 'axios';
import './Changepass.css';
import Header from './Header';

function Changepass() {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false); // State to handle form submission

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    // Basic validation to check if the new passwords match
    if (newPassword !== confirmPassword) {
      alert("New Password and Confirm Password do not match");
      return;
    }

    setIsLoading(true); // Disable the button while the request is in process

    try {
      const token = sessionStorage.getItem('userToken');
      const response = await axios.post(
        'http://localhost:3046/api/v1/users/passwordChange',
        {
          currentPassword,
          newPassword,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200 && response.data.success) {
        alert('Password changed successfully');
        // Clear the form fields after success
        setCurrentPassword('');
        setNewPassword('');
        setConfirmPassword('');
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Error changing password';
      console.error('Error changing password:', errorMessage);
      alert(errorMessage);
    } finally {
      setIsLoading(false); // Re-enable the button after the request completes
    }
  };

  return (
    <>
      <Header />
      <section id='contact' className='hero'>
        <div className='text'>
          <h4 className=''>CONTACT US NOW</h4>
          <h2>KEEP <span>IN TOUCH</span></h2>
          <ul className='row'>
            <a href='/' className='col'>
              <li className='inline'>HOME</li>
            </a>
            <a href='#' className='col ms-3'>
              <li>CONTACT US</li>
            </a>
          </ul>
        </div>
      </section>

      <div className="container-fluid">
        <div className="row">
          <div className="achive1 col-12 col-md-6 mb-4 mb-md-0">
            <div className="image-container">
              <img src="/company-age-bg.jpg" className="img-fluid background-image" alt="Background Image" />
              <img src="/company-banner.png" className="img-fluid overlay-image" alt="Overlay Image" />
            </div>
          </div>
          <div className='contactform col-md-6 ps-5'>
            <form onSubmit={handlePasswordChange}>
              <div className="mb-3 col-8 pt-5">
                <h2 className='text-center'>Change Password</h2>
                <h6><label>Current Password</label></h6>
                <input
                  type="password"
                  className="form-control"
                  placeholder='Current Password*'
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                />
              </div>
              <div className="mb-3 col-8">
                <h6><label>New Password</label></h6>
                <input
                  type="password"
                  className="form-control"
                  placeholder='New Password*'
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
              <div className="mb-3 col-8">
                <h6><label>Confirm New Password</label></h6>
                <input
                  type="password"
                  className="form-control"
                  placeholder='Confirm New Password*'
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
              </div>
              <div className="mb-3 col-8">
                <button type="submit" className="btn btn-primary col-12" disabled={isLoading}>
                  {isLoading ? 'Changing...' : 'Change'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default Changepass;
