import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ContactMessages.css';

const ContactMessages = () => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Fetch data from the API
    axios.get('http://localhost:3046/api/v1/contact/allmessage')
      .then(response => {
        console.log('API Response:', response.data); // Log the response to see the structure

        // Check if the response contains the 'data' field with the message array
        if (response.data && Array.isArray(response.data.data)) {
          setMessages(response.data.data);
        } else {
          console.error('Unexpected response structure:', response.data);
        }
      })
      .catch(error => {
        console.error('Error fetching messages:', error);
      });
  }, []);

  return (
    <div className="contact-messages-container">
      <h1>Contact Messages</h1>
      <div className="messages-list">
        {messages.length > 0 ? (
          messages.map((message, index) => (
            <div key={index} className="message-card">
              <h2>{message.fullName}</h2>
              <p><strong>Email:</strong> {message.email}</p>
              <p><strong>Mobile No:</strong> {message.mobile_no}</p>
              <p><strong>Message:</strong> {message.message}</p>
            </div>
          ))
        ) : (
          <p>No messages available.</p>
        )}
      </div>
    </div>
  );
};

export default ContactMessages;
