import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios for API calls
import Admin from './Admin'; // Assuming Admin is your admin panel component
import './Alluser.css'; // Your styles for Alluser component
import { toast } from 'react-toastify'; // Import toast for notifications

function Alluser() {
  const [users, setUsers] = useState([]); // State to store users
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(null); 
  const [ref, setRef] = useState(0); 

 
  const fetchAllUsers = async () => {
    const adminToken = sessionStorage.getItem('adminToken'); // Retrieve admin token from session storage
    if (!adminToken) {
      setError('No admin token found. Please log in again.'); // Error if token is missing
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get('http://localhost:3046/api/v1/admin/getalluser', {
        headers: {
          'Authorization': adminToken, // Send admin token in headers
        },
      });
      
      console.log('Fetch Users Response:', response.data);
      if (response.data.success && Array.isArray(response.data.data)) {
        setUsers(response.data.data); // Store the users in state
      } else {
        setError('Invalid response data');
      }
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Error fetching users');
    } finally {
      setLoading(false); // Set loading to false after API call
    }
  };

  // Function to handle block/unblock action
  const handleBlockUnblock = async (id) => {
    const data = { _id: id }; // Create the data object with the user ID
    try {
      const response = await axios.post("http://localhost:3046/api/v1/admin/blockandunblockuser", data, {
        headers: {
          Authorization: sessionStorage.getItem("adminToken"), // Send admin token in headers
        },
      });
      if (response.data.success) {
        setRef(Math.random()); // Update state to refresh the user list
        // toast.success(response.data.message); // Show success message
        fetchAllUsers(); // Re-fetch the user list
      }
    } catch (error) {
      console.error('Error updating user status:', error); // Catch and log any errors
      // toast.error('Failed to update user status'); // Show error message
    }
  };

  // Use effect to fetch users when the component is mounted
  useEffect(() => {
    fetchAllUsers();
  }, [ref]); // Dependency on ref to re-fetch users when it changes

  // Function to render table rows for users
  const renderTableRows = () => {
    return users.map((user, index) => (
      <tr key={user._id}>
        <td>{index + 1}</td>
        <td className='icon'>
          <img className='adminicon' src={user.avatar || '/adminicon.jpg'} alt='Profile' />
        </td>
        <td className='d-none d-md-table-cell'>{user.fullName || 'N/A'}</td>
        <td>{user.email || 'N/A'}</td>
        <td className='d-none d-md-table-cell'>{user.gender || 'N/A'}</td>
        <td className='d-none d-md-table-cell'>{user.mobile_no || 'N/A'}</td>
        <td>
          {user.block ? (
            <button
              onClick={() => handleBlockUnblock(user._id)}
              className=" text-white p-2 rounded-lg font-bold cursor-pointer bg-success"
            >
              Block
            </button>
          ) : (
            <button
              onClick={() => handleBlockUnblock(user._id)}
              className=" text-white p-2 rounded-lg font-bold cursor-pointer bg-success"
            >
              Unblock
            </button>
          )}
        </td>
      </tr>
    ));
  };

  return (
    <>
      <Admin /> {/* Assuming this is your Admin panel component */}
      <div className='userlist'>
        <div className='colorwhite'>
          {loading ? (
            <div>Loading users...</div> // Display loading message while data is being fetched
          ) : error ? (
            <div>{error}</div> // Display error message if there is an error
          ) : (
            <table className='alluser bg-white text-black'>
              <thead>
                <tr className='userheader'>
                  <th>No</th>
                  <th>Profile</th>
                  <th className='d-none d-md-table-cell'>Name</th>
                  <th>Email</th>
                  <th className='d-none d-md-table-cell'>Gender</th>
                  <th className='d-none d-md-table-cell'>Phone Number</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody >
                {renderTableRows()} {/* Render user table rows */}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </>
  );
}

export default Alluser;
