import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ContactMessages.css';
import Cookies from 'js-cookie'; // If using cookies for storing token

const ContactMessages = () => {
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch data from the API with authorization
    const token = sessionStorage.getItem("contactToken") || Cookies.get("contactToken");

    axios.get('http://localhost:3046/api/v1/contact/allmessage', {
      headers: {
        Authorization: `${token}`, // Attach token to the request
      },
    })
    .then(response => {
      console.log('Full API Response:', response); // Log the entire response object
      
      // Handle unexpected structure
      if (response.data && response.data.success && Array.isArray(response.data.data)) {
        setMessages(response.data.data); // If the structure matches, store the data
      } else {
        console.error('Unexpected response structure:', response.data);
        setError('Unexpected response structure.');
      }
    })
    .catch(error => {
      console.error('Error fetching messages:', error);
      setError('Error fetching messages.');
    });
  }, []);

  return (
    <div className="contact-messages-container">
      <h1>Contact Messages</h1>
      {error && <p className="error">{error}</p>}
      <div className="messages-list">
        {messages.length > 0 ? (
          messages.map((message, index) => (
            <div key={index} className="message-card">
              <h2>{message.fullName}</h2>
              <p><strong>Email:</strong> {message.email}</p>
              <p><strong>Mobile No:</strong> {message.mobile_no}</p>
              <p><strong>Message:</strong> {message.message}</p>
            </div>
          ))
        ) : (
          !error && <p>No messages available.</p>
        )}
      </div>
    </div>
  );
};

export default ContactMessages;
